class On4u_Gestitrans_Block_Onepage_Shipping_Method_Available extends Mage_Checkout_Block_Onepage_Shipping_Method_Available
{

    public function getShippingRates()
    {
        $rates = parent::getShippingRates();
        if (array_key_exists('Gestitrans', $rates)) {  
           $gestitrans = new On4u_Gestitrans_Model_On4u_Gestitrans();
           $orden = $gestitrans->getConfigData('sort');
           usort($rates['Gestitrans'], array($this, $orden));   
        }
        return $rates;
    }
    
    function tiempo($a, $b) {
        if ($a->getMethodDescription() == $b->getMethodDescription()) {
            return 0;
        }
        return ($a->getMethodDescription() < $b->getMethodDescription()) ? -1 : 1;
    }
    
    function precio($a, $b) {
        if ($a->getPrice() == $b->getPrice()) {
            return 0;
        }
        return ($a->getPrice() < $b->getPrice()) ? -1 : 1;
    }

